import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import React, { useCallback, useEffect, useState } from 'react';
import { SafeAreaView, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View, ImageBackground } from 'react-native';
import Fundo from '../../assets/FundoTelaUser.png';
import * as Font from 'expo-font';

import UserDao from '../../db/User';
import { TextFont } from '../../components/style';

export default function GetNomeUser() {
    const navigation = useNavigation()

    const [nomeUser, setNomeUser] = useState('');

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <ImageBackground source={Fundo} style={{ flex: 1, backgroundColor: '#59D89E', justifyContent: 'center', alignItems: 'center' }}>
                <ScrollView contentContainerStyle={{ flex: 1, justifyContent: 'center' }}>
                    <View style={{ justifyContent: 'center', alignItems: 'center' }}><TextFont style={styles.titleTest}>LOCALITY MAP</TextFont></View>
                    <View style={styles.marginView}>
                        <View>
                            <TextInput
                                focusable={false}
                                style={styles.input}
                                onChangeText={setNomeUser}
                                value={nomeUser}
                                placeholder="Seu apelido"
                                keyboardType="default"
                                placeholderTextColor='white'
                            />
                        </View>
                    </View>
                    <View style={{ marginTop: 50, width: '100%', alignItems: 'center' }}>
                        <TouchableOpacity
                            style={styles.button}
                            onPress={() => navigation.navigate("Contatos")}
                        >
                            <Text style={{ color: 'white' }}>ENTRAR</Text>
                        </TouchableOpacity>
                    </View>
                </ScrollView>
            </ImageBackground>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center'
    },
    titleTest: {
        marginLeft: 10,
        fontSize: 20,
        fontWeight: 'bold'
    },
    input: {
        backgroundColor: 'transparent',
        color: 'white',
        height: 35,
        paddingHorizontal: 20,
        borderColor: 10,
        borderWidth: 1,
        borderColor: '#5DA2FF',
        borderRadius: 50,
        width: 250
    },
    marginView: {
        paddingHorizontal: 10,
        marginVertical: 20
    },
    viewButton: {
        marginTop: 10
    },
    button: {
        borderRadius: 20,
        backgroundColor: '#2B44FF',
        borderColor: 'white',
        '&:focus': {
            borderColor: '#1820C0'
        },
        color: 'white',
        padding: 10,
        width: 200,
        alignItems: 'center'
    }
})
