import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import React, { useCallback, useEffect, useState } from 'react';
import { SafeAreaView, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View, ImageBackground } from 'react-native';
import Fundo from '../../assets/FundoTelaUser.png';

import UserDao from '../../db/User';

export default function GetNomeUser() {
    const navigation = useNavigation()

    const [nomeUser, setNomeUser] = useState('');

    useEffect(useCallback(() => {
        const get = async () => {
            const user = await UserDao.GetUser()
            console.log(user)
        }
        get()
    }, []))

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <ImageBackground source={Fundo} style={{ flex: 1, backgroundColor: '#59D89E', justifyContent: 'center', alignItems: 'center', alignContent: 'center' }}>
                <ScrollView contentContainerStyle={{ justifyContent: 'center', height: '50%', width: '50%' }}>
                    <Text style={styles.titleTest}>Sign in</Text>
                    <View style={styles.marginView}>
                        <View>
                            <TextInput
                                style={styles.input}
                                onChangeText={setNomeUser}
                                value={nomeUser}
                                placeholder="Seu apelido"
                                keyboardType="default"
                            />
                        </View>
                    </View>
                    <View style={{ marginTop: 20, width: '100%', alignItems: 'center' }}>
                        <TouchableOpacity
                            style={styles.button}
                            onPress={() => navigation.navigate("Contatos")}
                        >
                            <Text style={{ color: 'white' }}>ENTRAR</Text>
                        </TouchableOpacity>
                    </View>
                </ScrollView>
            </ImageBackground>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center'
    },
    titleTest: {
        marginLeft: 10,
        fontSize: 15,
        fontWeight: 'bold'
    },
    input: {
        backgroundColor: 'transparent',
        color: 'black',
        height: 35,
        paddingHorizontal: 20,
        borderColor: 10,
        borderWidth: 0.7,
        borderRadius: 50
    },
    marginView: {
        paddingHorizontal: 10,
        marginVertical: 20
    },
    viewButton: {
        marginTop: 10
    },
    button: {
        borderRadius: 20,
        backgroundColor: '#2B44FF',
        borderColor: 'white',
        '&:focus': {
            borderColor: '#1820C0'
        },
        '&:hover': {
            borderColor: '#1820C0'
        },
        color: 'white',
        padding: 10,
        width: '50%',
        alignItems: 'center'
    }
})
